import React from "react";


export default function FourCircleVenn({
size = 400,
labels = ["A", "B", "C", "D"],
colors = ["#ef4444", "#f59e0b", "#10b981", "#3b82f6"]
}) {
const r = size * 0.2; // radio de cada círculo
const cx = size / 2;
const cy = size / 2;


// Posiciones en un patrón de superposición (Venn 4 círculos)
const offset = r * 0.8;
const positions = [
{ x: cx - offset, y: cy - offset },
{ x: cx + offset, y: cy - offset },
{ x: cx - offset, y: cy + offset },
{ x: cx + offset, y: cy + offset }
];


return (
<div className="p-4 flex justify-center">
<svg viewBox={`0 0 ${size} ${size}`} width={size} height={size}>
{positions.map((pos, i) => (
<g key={i}>
<circle
cx={pos.x}
cy={pos.y}
r={r}
fill={colors[i % colors.length]}
fillOpacity={1.5} // opacidad parcial para que se vea superposición sin mezclar colores
stroke="black"
strokeWidth={2}
/>
<text
x={pos.x}
y={pos.y}
textAnchor="middle"
alignmentBaseline="middle"
fontSize={size * 0.08}
fontWeight={600}
fill="#111827"
>
{labels[i]}
</text>
</g>
))}
</svg>
</div>
);
}
