import React, {useState} from 'react'
import styles from '../../styles/BurgerMenu.module.css'
import { NavLink } from 'react-router-dom';
import BurgerMenuLinkList from './BurgerMenuLinkList';

function BurgerMenu (){
    const sectionLinkList = ["About", "Experience", "Skills", "Education", "Contact"];
    const [linkListDisplay, setLinkListDisplay] = useState({display: "flex", flexdirection:"column" }
    );

    const outHome = () => {
        setLinkListDisplay({display: "none",})
    };
    
    const onHome = () => {
        setLinkListDisplay({flexdirection:"column", display: "flex", })
    };
    

    // to change burger classes
    const [burger_class, setBurgerClass] = useState(`${styles.burgerBar} ${styles.unclicked}`)
    const [menu_class, setMenuClass] = useState(`${styles.menu} ${styles.hidden}`)
    const [isMenuClicked, setIsMenuClicked] = useState(false)

    // toggle burger menu change
    const updateMenu = () => {
        setIsMenuClicked(!isMenuClicked)
        if(isMenuClicked) {
            setBurgerClass(`${styles.burgerBar} ${styles.clicked}`)
            setMenuClass(`${styles.menu} ${styles.visible}`)
        }
        else {
            setBurgerClass(`${styles.burgerBar} ${styles.unclicked}`)
            setMenuClass(`${styles.menu} ${styles.hidden}`)
        }
    }

    const hoverEffect = (e) => {
        e.currentTarget.style.color = "var(--clr-funct-bg2)"
        e.currentTarget.style.transform = "translateY(-2px)";
    };

    const leaveEffect = (e) => {
        e.currentTarget.style.color = "var(--clr-funct-font)";
        e.currentTarget.style.transform = "translateY(0)";
    };

    const buildHref = (item) => {
        return '#' + item.toLowerCase().replace(/\s/g,);
    }

    return(
        <div className={styles.spacer}>
                <div  className={styles.fullmenu}>
                    <BurgerMenuLinkList></BurgerMenuLinkList>

                    <div className={styles.burgerMenu} onClick={updateMenu}>
                        <div className={burger_class}></div>
                        <div className={burger_class}></div>
                        <div className={burger_class}></div>
                    </div>
                </div>

            <div className={menu_class} >
                <div className={styles.navLinksList}>
                    <BurgerMenuLinkList></BurgerMenuLinkList>
                </div>
            </div>
        </div>
    )
}


export default BurgerMenu;
