import React from "react";
import styles from "../../styles/BlogPreviewCard.module.css";
import LinkButton from "../general/LinkButton";

export default function BlogPreviewCard({ id, title, excerpt, author, date }) {
  return (
    <article className={styles.card}>
      <header className={styles.header}>
        <h3>{title}</h3>
        <p className={styles.meta}>
          {author} â€¢ {new Date(date).toLocaleDateString()}
        </p>
      </header>
      <p>{excerpt}</p>
      <LinkButton text="Read more" href={"/post/"+id}></LinkButton>
    </article>
  );
}
