import React, { useEffect, useState } from "react";


export default function FiveCircleVenn({
labels = ["A", "B", "C", "D", "E"],
colors = ["#ef4444", "#f59e0b", "#10b981", "#3b82f6", "#8b5cf6"],
centerLabelTop = "Ver칩nica",
centerLabelBottom = "S치nchez Justicia"
}) {
const [size, setSize] = useState(Math.min(window.innerWidth, window.innerHeight));


useEffect(() => {
const handleResize = () => setSize(Math.min(window.innerWidth, window.innerHeight));
window.addEventListener('resize', handleResize);
return () => window.removeEventListener('resize', handleResize);
}, []);


const r = size * 0.22;
const cx = size / 2;
const cy = size / 2;
const radiusFromCenter = r * 0.8;
const angleOffset = -Math.PI / 2;


const positions = Array.from({ length: 5 }).map((_, i) => {
const a = (i * (2 * Math.PI)) / 5 + angleOffset;
return {
x: Math.cos(a) * radiusFromCenter,
y: Math.sin(a) * radiusFromCenter,
angle: a,
label: labels[i],
color: colors[i % colors.length]
};
});


const [angle, setAngle] = useState(0);
}
