import React from "react";
import imgSelfie from '../../images/selfie.png';
import infoStyles from '../../styles/navHeader.module.css';

function NavHeader() {

  const hoverEffect = (e) => {
    e.currentTarget.style.color = "var(--clr-funct-bg2)"
    e.currentTarget.style.transform = "translateY(-2px)";
  };

  const leaveEffect = (e) => {
    e.currentTarget.style.color = "var(--clr-funct)";
    e.currentTarget.style.transform = "translateY(0)";
  };

  return (
    <header className={infoStyles.header}>
        <div className={infoStyles.imgContainer}>
              <img src={imgSelfie} alt="Selfie"
                  className={infoStyles.imgSelfie} ></img>
        </div>

        <h1 className={infoStyles.logo}>Veronica Sanchez Justicia</h1>
        <nav className={infoStyles.nav}>
          {["About", "Experience", "Skills", "Education", "Contact"].map((item, idx) => (
            <a
              key={idx}
              href={`#${item.toLowerCase().replace(/\s/g, "")}`}
              className={infoStyles.link}
              onMouseEnter={hoverEffect}
              onMouseLeave={leaveEffect}
            >
              {item}
            </a>
          ))}
        </nav>
    </header>
  );
}

export default NavHeader;
