import React from "react";

// Componente: FiveCircleVenn
// Uso: import FiveCircleVenn from './FiveCircleVenn'
// <FiveCircleVenn size={600} labels={["A","B","C","D","E"]} />

export default function FiveCircleVenn({
  size = 600,
  labels = ["A", "B", "C", "D", "E"],
  colors = [
    "#ef4444", // red-500
    "#f59e0b", // amber-500
    "#10b981", // emerald-500
    "#3b82f6", // blue-500
    "#8b5cf6", // violet-500
  ],
  showLabels = true,
}) {
  const r = size * 0.24; // radio de cada círculo
  const cx = size / 2;
  const cy = size / 2;

  // Posiciones en un pentágono para cinco círculos (con solapamiento)
  const angleOffset = -Math.PI / 2;
  const radiusFromCenter = size * 0.28;

  const positions = Array.from({ length: 5 }).map((_, i) => {
    const a = (i * (2 * Math.PI)) / 5 + angleOffset;
    return {
      x: cx + Math.cos(a) * radiusFromCenter,
      y: cy + Math.sin(a) * radiusFromCenter,
    };
  });

  return (
    <div className="p-4 max-w-full flex flex-col items-center gap-4">
      <div className="w-full flex justify-center">
        <svg
          viewBox={`0 0 ${size} ${size}`}
          width={size}
          height={size}
          className="shadow-2xl rounded-2xl bg-white"
          role="img"
          aria-label="Diagrama de Venn de 5 círculos"
        >
          {/* Dibujar los 5 círculos */}
          {positions.map((pos, i) => (
            <g key={i} className="group">
              <circle
                cx={pos.x}
                cy={pos.y}
                r={r}
                fill={colors[i % colors.length]}
                fillOpacity={1}
                stroke="rgba(0,0,0,0.2)"
                strokeWidth={2}
              />

              {showLabels && (
                <text
                  x={pos.x}
                  y={pos.y - r - 8}
                  textAnchor="middle"
                  fontSize={size * 0.035}
                  fontWeight={600}
                  fill="#111827"
                  className="pointer-events-none"
                >
                  {labels[i]}
                </text>
              )}

              <title>{`Conjunto ${labels[i]}`}</title>
            </g>
          ))}

          {/* Semicírculo sobre el primer círculo, aplicado desde el último círculo */}
          {(() => {
            const first = positions[0];
            const last = positions[4];
            const lastColor = colors[4 % colors.length];
            // Semicírculo sobre el primer círculo con color del último círculo
            return (
              <circle
                d={`M ${first.x},${first.y - r} A ${r},${r} 0 0,1 ${first.x},${first.y + r} L ${first.x},${first.y} Z`}
                color="red"
                
              />
            );
          })()}

          {/* centro — etiqueta que indica la intersección general */}
          <g>
            <circle
              cx={cx}
              cy={cy}
              r={size * 0.06}
              fill="transparent"
              stroke="rgba(0,0,0,0.2)"
            />
            <text
              x={cx}
              y={cy}
              textAnchor="middle"
              alignmentBaseline="middle"
              fontSize={size * 0.035}
              fontWeight={700}
              fill="#111827"
            >
              {"A∩B∩C∩D∩E"}
            </text>
          </g>
        </svg>
      </div>

      <div className="w-full max-w-xl text-sm text-gray-600">
        <p>
          Este componente dibuja cinco círculos con stacking circular simulado:
          cada círculo queda por encima del siguiente y por debajo del anterior,
          utilizando un semicírculo del último círculo sobre el primero.
        </p>
      </div>
    </div>
  );
}
