import React from "react";
import { FaWhatsapp, FaEnvelope, FaLinkedin, FaGithub, FaGlobe } from "react-icons/fa";

import  ContactStyle from '../../styles/FloatingContactBar.module.css';

export default function FloatingContactBar() {
  
  const [isOpen, setIsOpen] = useState(false);

  const listStyle= {
      display: isOpen ? "block" : "none",
  }
  
  const tooltipStyle= {
      color: isOpen ? "var(--clr-funct-bg2)" : "var(--clr-funct-high-light-1)" ,
  }

  const hoverEffect = (e) => {
    e.currentTarget.style.backgroundColor = "var(--clr-funct-high-light-1)";
    
  };

  const leaveEffect = (e) => {
    e.currentTarget.style.backgroundColor = "var(--clr-funct-bg2)";
    
  };

  const showTooltip = (e) => {
    e.currentTarget.querySelector("svg").style.opacity = 1;
    e.currentTarget.style.transform = "scale(1.2)";
    setIsOpen(true)
  };

  const hideTooltip = (e) => {
    e.currentTarget.querySelector("svg").style.opacity = 0;
    e.currentTarget.style.transform = "scale(1)";
    setIsOpen(false)

  };

  const contacts = [
    {
      icon: <FaWhatsapp />,
      href: "https://wa.me/34644048205",
      tooltip: "+34 644 048 205",
      target: "_blank",
    },
    {
      icon: <FaEnvelope />,
      href: "mailto:veronicasanchezjusticia2@gmail.com",
      tooltip: "veronicasanchezjusticia2@gmail.com",
      target: "_self",
    },
    {
      icon: <FaLinkedin />,
      href: "https://www.linkedin.com/in/ver%C3%B3nica-s%C3%A1nchez-justicia-825a7421a/",
      tooltip: "LinkedIn",
      target: "_blank",
    },
    {
      icon: <FaGlobe />,
      href: "https://veronicasj.github.io/Portfolio/",
      tooltip: "Portfolio",
      target: "_blank",
    },
    {
      icon: <FaGithub />,
      href: "https://github.com/VeronicaSj",
      tooltip: "GitHub",
      target: "_blank",
    },
  ];

  return (
    <div className={ContactStyle.bar}>
      {contacts.map((contact, index) => (
        <a
          key={index}
          href={contact.href}
          target={contact.target}
          rel="noreferrer"
          className={ContactStyle.iconContainer}
          onMouseEnter={showTooltip}
          onMouseLeave={hideTooltip}
        >
          <span className={ContactStyle.tooltip}>
          </span>
          {contact.icon}
        </a>
      ))}
    </div>
  );
}
