import React, { useEffect, useState } from "react";

export default function FiveCircleVenn({
  size = 400,
  labels = ["A", "B", "C", "D", "E"],
  colors = ["#ef4444", "#f59e0b", "#10b981", "#3b82f6", "#8b5cf6"]
}) {
  const r = size * 0.22;
  const cx = size / 2;
  const cy = size / 2;
  const radiusFromCenter = r * 0.8;
  const angleOffset = -Math.PI / 2;

  const positions = Array.from({ length: 5 }).map((_, i) => {
    const a = (i * (2 * Math.PI)) / 5 + angleOffset;
    return {
      x: cx + Math.cos(a) * radiusFromCenter,
      y: cy + Math.sin(a) * radiusFromCenter,
      angle: a,
      label: labels[i],
      color: colors[i % colors.length]
    };
  });

  const [angle, setAngle] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setAngle(prev => prev + 0.01);
    }, 16);
    return () => clearInterval(interval);
  }, []);

  // Factor de desplazamiento para alejar el texto del centro
  const textOffset = radiusFromCenter + r + 20;

  return (
    <div className="p-4 flex justify-center">
      <svg viewBox={`0 0 ${size} ${size}`} width={size} height={size}>
        <defs>
          <radialGradient id="centerGradient" cx="50%" cy="50%" r="50%">
            <stop offset="0%" stopColor="white" stopOpacity={1} />
            <stop offset="100%" stopColor="white" stopOpacity={0} />
          </radialGradient>
        </defs>

        {/* Grupo de círculos que gira */}
        <g transform={`rotate(${(angle * 180) / Math.PI}, ${cx}, ${cy})`}>
          {positions.map((pos, i) => (
            <circle
              key={i}
              cx={pos.x}
              cy={pos.y}
              r={r}
              fill={pos.color}
              fillOpacity={0.5}
              stroke="black"
              strokeWidth={2}
            />
          ))}
        </g>

        {/* Texto centrado en cada círculo pero alejado del centro */}
        {positions.map((pos, i) => {
          const textX = cx + Math.cos(pos.angle) * textOffset;
          const textY = cy + Math.sin(pos.angle) * textOffset;
          return (
            <text
              key={i}
              x={textX}
              y={textY}
              textAnchor="middle"
              alignmentBaseline="middle"
              fontSize={size * 0.06}
              fontWeight={700}
              fill="#111827"
              transform={`rotate(${-angle * 180 / Math.PI}, ${textX}, ${textY})`}
            >
              {pos.label}
            </text>
          );
        })}

        {/* Centro con degradado blanco */}
        <circle cx={cx} cy={cy} r={r * 0.8} fill="url(#centerGradient)" />
      </svg>
    </div>
  );
}
