import React from "react";


export default function FiveCircleVenn({
size = 400,
labels = ["A", "B", "C", "D", "E"],
colors = ["#ef4444", "#f59e0b", "#10b981", "#3b82f6", "#8b5cf6"]
}) {
const r = size * 0.22; // radio ajustado para 5 círculos
const cx = size / 2;
const cy = size / 2;


// Posiciones en pentágono para 5 círculos
const angleOffset = -Math.PI / 2;
const radiusFromCenter = r * 1.2;
const positions = Array.from({ length: 5 }).map((_, i) => {
const a = (i * (2 * Math.PI)) / 5 + angleOffset;
return {
x: cx + Math.cos(a) * radiusFromCenter,
y: cy + Math.sin(a) * radiusFromCenter
};
});


return (
<div className="p-4 flex justify-center">
<svg viewBox={`0 0 ${size} ${size}`} width={size} height={size}>
{/* Círculos superpuestos */}
{positions.map((pos, i) => (
<g key={i}>
<circle
cx={pos.x}
cy={pos.y}
r={r}
fill={colors[i % colors.length]}
fillOpacity={0.5}
stroke="black"
strokeWidth={2}
/>
<text
x={pos.x}
y={pos.y}
textAnchor="middle"
alignmentBaseline="middle"
fontSize={size * 0.06}
fontWeight={600}
fill="#111827"
>
{labels[i]}
</text>
</g>
))}
</svg>
</div>
);
}
