import React from "react";

export default function FourCircleVenn({
  size = 400,
  labels = ["A", "B", "C", "D"],
  colors = ["#ef4444", "#f59e0b", "#10b981", "#3b82f6"]
}) {
  const r = size * 0.25; // radio más grande para acercar los círculos
  const cx = size / 2;
  const cy = size / 2;

  // Posiciones más juntas para los 4 círculos
  const offset = r * 0.6;
  const positions = [
    { x: cx - offset, y: cy - offset },
    { x: cx + offset, y: cy - offset },
    { x: cx - offset, y: cy + offset },
    { x: cx + offset, y: cy + offset }
  ];

  return (
    <div className="p-4 flex justify-center">
      <svg viewBox={`0 0 ${size} ${size}`} width={size} height={size}>
        {/* Círculos superpuestos */}
        {positions.map((pos, i) => (
          <g key={i}>
            <circle
              cx={pos.x}
              cy={pos.y}
              r={r}
              fill={colors[i % colors.length]}
              fillOpacity={1.5}
              stroke="black"
              strokeWidth={2}
            />
            <text
              x={pos.x}
              y={pos.y}
              textAnchor="middle"
              alignmentBaseline="middle"
              fontSize={size * 0.08}
              fontWeight={600}
              fill="#111827"
            >
              {labels[i]}
            </text>
          </g>
        ))}

      </svg>
    </div>
  );
}
