import React, { useEffect, useState } from "react";

export default function FiveCircleVenn({
  size = 400,
  labels = ["A", "B", "C", "D", "E"],
  colors = ["#ef4444", "#f59e0b", "#10b981", "#3b82f6", "#8b5cf6"]
}) {
  const r = size * 0.22;
  const cx = size / 2;
  const cy = size / 2;
  const radiusFromCenter = r * 0.8;
  const angleOffset = -Math.PI / 2;

  const positions = Array.from({ length: 5 }).map((_, i) => {
    const a = (i * (2 * Math.PI)) / 5 + angleOffset;
    return {
      x: cx + Math.cos(a) * radiusFromCenter,
      y: cy + Math.sin(a) * radiusFromCenter,
      angle: a
    };
  });

  const [angle, setAngle] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setAngle(prev => prev + 0.01);
    }, 16);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="p-4 flex justify-center">
      <svg viewBox={`0 0 ${size} ${size}`} width={size} height={size}>
        <defs>
          <radialGradient id="centerGradient" cx="50%" cy="50%" r="50%">
            <stop offset="0%" stopColor="white" stopOpacity={1} />
            <stop offset="100%" stopColor="white" stopOpacity={0} />
          </radialGradient>
        </defs>

        {/* Grupo de c√≠rculos y texto que gira */}
        <g transform={`rotate(${(angle * 180) / Math.PI}, ${cx}, ${cy})`}>
          {positions.map((pos, i) => {
            const textRadius = r + 20;
            const textX = cx + Math.cos(pos.angle) * textRadius;
            const textY = cy + Math.sin(pos.angle) * textRadius;
            return (
              <g key={i}>
                <circle
                  cx={pos.x}
                  cy={pos.y}
                  r={r}
                  fill={colors[i % colors.length]}
                  fillOpacity={0.5}
                  stroke="black"
                  strokeWidth={2}
                />
                <text
                  x={textX}
                  y={textY}
                  textAnchor="middle"
                  alignmentBaseline="middle"
                  fontSize={size * 0.06}
                  fontWeight={700}
                  fill="#111827"
                >
                  {labels[i]}
                </text>
              </g>
            );
          })}
        </g>

        {/* Centro con degradado blanco */}
        <circle cx={cx} cy={cy} r={r * 0.8} fill="url(#centerGradient)" />
      </svg>
    </div>
  );
}
